list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX bibo: <http://purl.org/ontology/bibo/>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX uowvivo: <http://uowvivo.uow.edu.au/ontology/uowvivo#>

SELECT DISTINCT ?subclass
    ?authorship
    ?infoResource ?infoResourceName
    ?dateTime
    ?journal
    ?volume
    ?startPage
    ?endPage
    ?publisher
    ?locale
    ?appearsIn
    ?partOf
    ?editor
    ?hideThis
    ?quotation ?bookTitle ?issn
    ?coursewareVersion
    ?dateOfExhibition
    ?ismn
    ?dateOfAd
    ?placeOfAd
    ?dateFirstPerformed
    ?lengthOfPerformance
    ?dateOfRecording
    ?edition
    ?issue
    ?url
    ?numPages
    ?isbn10
    ?isbn13
    ?venue
    ?conferenceTitle
    ?conferenceLocat
    ?dateOfPresentation
    ?patentNumber
    ?doi
WHERE
{
    ?subject ?property ?authorship .
    ?authorship a core:Authorship .
    ?authorship core:relates ?infoResource .
    ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
    ?infoResource rdfs:label ?infoResourceName .
    ?infoResource vitro:mostSpecificType ?subclass .
    FILTER (!regex(str(?subclass), "InformationResource","i")) .
    FILTER (!regex(str(?subclass), "/Document","i")) .
    OPTIONAL { ?infoResource bibo:volume ?volume }
    OPTIONAL { ?infoResource bibo:pageStart ?startPage }
    OPTIONAL { ?infoResource bibo:pageEnd ?endPage }
    OPTIONAL { ?infoResource bibo:numPages ?numPages }
    OPTIONAL { ?infoResource bibo:isbn10 ?isbn10 }
    OPTIONAL { ?infoResource bibo:isbn13 ?isbn13 }
    OPTIONAL { ?infoResource core:placeOfPublication ?locale }
    OPTIONAL
    {
        ?infoResource bibo:reproducedIn ?appearsInObj .
        ?appearsInObj rdfs:label ?appearsIn .
    }
    OPTIONAL
    {
        ?infoResource core:publisher ?publisher .
        #?publisherObj rdfs:label ?publisher .
    }
    OPTIONAL
    {
        ?infoResource <http://purl.obolibrary.org/obo/BFO_0000050> ?partOfObj .
        ?partOfObj rdfs:label ?partOf .
    }
    OPTIONAL
    {
        ?infoResource core:hasPublicationVenue ?publishedIn .
        ?publishedIn  rdfs:label ?journal .
    }
    OPTIONAL { ?infoResource core:hasPublicationVenue ?publishedIn .
        ?publishedIn  bibo:issn  ?issn
    }
    OPTIONAL
    {
        ?infoResource core:dateTimeValue ?dateTimeValue .
        ?dateTimeValue core:dateTime ?dateTime .
    }
    OPTIONAL
    {
        ?infoResource core:relatedBy ?editorship .
        ?editorship a core:Editorship .
        ?editorship core:relates ?editorObj .
        ?editorObj rdfs:label ?editor .
    }
    OPTIONAL { ?authorship core:hideFromDisplay ?hideThis }
    OPTIONAL { ?infoResource uowvivo:bookTitle ?bookTitle}
    OPTIONAL { ?infoResource uowvivo:coursewareVersion ?coursewareVersion}
    OPTIONAL { ?infoResource uowvivo:dateOfExhibition ?dateOfExhibition}
    OPTIONAL { ?infoResource uowvivo:ismn ?ismn}
    OPTIONAL { ?infoResource uowvivo:dateOfAdvertisement ?dateOfAd}
    OPTIONAL { ?infoResource uowvivo:placeOfAdvertisement ?placeOfAd}
    OPTIONAL { ?infoResource uowvivo:dateFirstPerformed ?dateFirstPerformed}
    OPTIONAL { ?infoResource uowvivo:lengthOfPerformance ?lengthOfPerformance}
    OPTIONAL { ?infoResource uowvivo:dateOfRecording ?dateOfRecording}
    OPTIONAL { ?infoResource uowvivo:url ?url}
    OPTIONAL { ?infoResource bibo:edition ?edition}
    OPTIONAL { ?infoResource bibo:issue ?issue}
    OPTIONAL { ?infoResource uowvivo:venue ?venue}
    OPTIONAL { ?infoResource bibo:presentedAt ?conferenceTitle}
    OPTIONAL { ?infoResource bibo:locator ?conferenceLocat}
    OPTIONAL { ?infoResource bibo:doi ?doi}
    OPTIONAL { ?infoResource uowvivo:dateOfPresentation ?dateOfPresentation}
    OPTIONAL { ?infoResource core:patentNumber ?patentNumber}
} ORDER BY ?subclass  DESC(?dateTime) ?infoResourceName LIMIT 200
