list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX uowimpact: <http://uowvivo.uow.edu.au/ontology/uowimpact#>

CONSTRUCT
{
    ?subject ?property ?object .
    ?object a ?objectType .
    ?object rdfs:label ?label .
    ?object uowimpact:hideFromPublic ?hideThis .
    ?object uowimpact:title ?title .
    ?object uowimpact:description ?description .
    ?object uowimpact:detailOfImpact ?detailOfImpact .
}
WHERE
{
    {
        ?subject ?property ?object .
        ?object a ?objectType .
    }
    UNION
    {
        ?subject ?property ?object .
        ?object rdfs:label ?label .
    }
    UNION
    {
        ?subject ?property ?object .
        ?object uowimpact:hideFromPublic ?hideThis .
    }
    UNION
    {
        ?subject ?property ?object .
        ?object uowimpact:title ?title .
    }
    UNION
    {
        ?subject ?property ?object .
        ?object uowimpact:description ?description .
    }
    UNION
    {
        ?subject ?property ?object .
        ?object uowimpact:detailOfImpact ?detailOfImpact .
    }
}
