list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX uowvivo: <http://uowvivo.uow.edu.au/ontology/uowvivo#>

SELECT DISTINCT ?object ?infoResource ?infoResourceName ?hideThis ?dateTime ?journalTitle
WHERE {
    ?subject ?property ?object .
    ?object vivo:relates ?infoResource .
    ?infoResource a <http://purl.obolibrary.org/obo/IAO_0000030> .
    ?infoResource rdfs:label ?infoResourceName
    OPTIONAL { ?object uowvivo:publicationRank ?pubrank }
    OPTIONAL { ?object vivo:hideFromDisplay ?hideThis }
    OPTIONAL { ?infoResource vivo:dateTimeValue ?dateTimeValue .
               ?dateTimeValue vivo:dateTime ?dateTime
             }
    OPTIONAL { ?infoResource a <http://purl.org/ontology/bibo/Document> .
                ?infoResource vivo:hasPublicationVenue ?journal .
                ?journal a <http://purl.org/ontology/bibo/Journal> .
                ?journal rdfs:label ?journalTitle
    }
} ORDER BY ?pubrank
